SELECT COUNT(*) FROM HUMANLIST

	
	AND OUTYN = 'N'
	AND OUTYN = 'Y'
	
	AND INDATE BETWEEN #{date} AND #{date2} 
	AND DCODE = #{dept}
	AND RCODE = #{rank}
	AND EEMAIL = #{email}
	AND  ENO = #{eno}
	AND  ENAME = #{name}

SELECT ENO, PWD, ENAME, EBIRTHDAY, EADDRESS, EEMAIL, EPHONE, INDATE, OUTDATE, OUTYN, RNAME, DNAME FROM HUMANLIST

	
	AND OUTYN = 'N'
	AND OUTYN = 'Y'
	
	AND INDATE BETWEEN #{date} AND #{date2} 
	AND DCODE = #{dept}
	AND RCODE = #{rank}
	AND EEMAIL = #{email}
	AND  ENO = #{eno}
	AND  ENAME = #{name}

SELECT * FROM TB_RANK

	AND rcode=#{rcode}
	AND rname=#{rname}

SELECT VNO, ENAME ENO, VTYPE, STARTDATE, ENDDATE, USEDAY,
	   REASON, REQUESTDATE, STATUS, DNAME, RNAME 
FROM VACATIONLIST

	
	AND STATUS = 'N'
	AND STATUS = 'Y'
	AND STATUS = 'F'
	
	AND REQUESTDATE BETWEEN #{date} AND #{date2}
	AND STARTDATE BETWEEN #{date} AND #{date2}
	AND ENDDATE BETWEEN #{date} AND #{date2}
	AND DCODE=#{selectDept}
	AND RCODE=#{selectRank}
	AND ENAME=#{name}

SELECT COUNT(*) FROM VACATIONLIST

	
	AND REQUESTDATE BETWEEN #{date} AND #{date2}
	AND DCODE=#{selectDept}
	AND RCODE=#{selectRank}
	AND ENO=#{eno}
	AND ENAME=#{name}

SELECT COUNT(*)
FROM WORKLIST W
	 JOIN TB_USER U ON(W.ENO=U.ENO)
	 JOIN TB_DEPT D ON (D.DCODE=U.DCODE)
	 JOIN TB_RANK R ON (R.RCODE=U.RCODE)

	
	AND OUTYN = 'N'
	AND OUTYN = 'Y'
	
	AND TO_CHAR(TO_DATE(HIRDATE), 'YYYY-MM') = #{date}
	AND D.DCODE = #{dept}
	AND R.RCODE = #{rank}
	AND  W.ENO = #{eno}
	AND  ENAME = #{name}

SELECT W.*
FROM WORKLIST W
	 JOIN TB_USER U ON(W.ENO=U.ENO)
	 JOIN TB_DEPT D ON (D.DCODE=U.DCODE)
	 JOIN TB_RANK R ON (R.RCODE=U.RCODE)

	
	AND OUTYN = 'N'
	AND OUTYN = 'Y'
	
	AND TO_CHAR(TO_DATE(HIRDATE), 'YYYY-MM') = #{date}
	AND D.DCODE = #{dept}
	AND R.RCODE = #{rank}
	AND  W.ENO = #{eno}
	AND  ENAME = #{name}

ORDER BY W.ENO, W.HIRDATE

SELECT U.ENAME, D.DNAME, R.RNAME
FROM (SELECT DISTINCT ENO, TO_CHAR(TO_DATE(HIRDATE), 'YYYY-MM') HIRDATE FROM WORKLIST) W
	 JOIN TB_USER U ON(W.ENO=U.ENO)
	 JOIN TB_DEPT D ON (D.DCODE=U.DCODE)
	 JOIN TB_RANK R ON (R.RCODE=U.RCODE)

	
	AND OUTYN = 'N'
	AND OUTYN = 'Y'
	
	AND HIRDATE = #{date}
	AND D.DCODE = #{dept}
	AND R.RCODE = #{rank}
	AND  W.ENO = #{eno}
	AND  ENAME = #{name}

ORDER BY W.ENO

