mapper.select:
SELECT USEDAY, (SELECT SUM(USEDAY) FROM TB_VACATION_RECORD WHERE ENO = #{eno}) AS ALLDAY
FROM TB_VACATION
WHERE ENO = #{eno}

SELECT COUNT(*)
FROM VACATIONLIST
WHERE ENO=#{eno}

mapper.insert:
INSERT INTO TB_PRODUCT_INOUT VALUES(
	#{ccode}, #{pcode}, #{status}, SYSDATE, 
	#{count}, null,
	null, #{count},
	#{comment}
)
