mapper.select:
SELECT PCODE, PNAME, COST_PRICE, SELLING_PRICE, P_COMMENT, CNAME, CCODE
FROM PRODUCTLIST
WHERE PCODE = #{pcode}

SELECT * FROM PRODUCTLIST

	AND CCODE = #{selectClient}
	AND PCODE = #{selectProduct}

SELECT COUNT(*) FROM SUPPLYLIST

	
		AND SCODE LIKE '%' || #{scode} || '%'
	
	
		AND SNAME LIKE '%' || #{sname} || '%'
	
	
		AND COST_PRICE BETWEEN #{price} AND #{price2}
	
	
		 AND COST_PRICE >= #{price} 
	
	
		 AND COST_PRICE <= #{price2}

SELECT * FROM SUPPLYLIST

	
		AND SCODE LIKE '%' || #{scode} || '%'
	
	
		AND SNAME LIKE '%' || #{sname} || '%'
	
	
		AND COST_PRICE BETWEEN #{price} AND #{price2}
	
	
		 AND COST_PRICE >= #{price} 
	
	
		 AND COST_PRICE <= #{price2}

SELECT COUNT(*) FROM PRODUCTLIST

	AND PCODE LIKE '%' || #{pcode} || '%'
	AND PNAME LIKE '%' || #{pname} || '%'
	 PCOUNT >= #{pcount} 
	
		AND COST_PRICE BETWEEN #{price} AND #{price2}
	
	
		 AND COST_PRICE >= #{price} 
	
	
		 AND COST_PRICE <= #{price2} 
	
	AND P_COMMENT LIKE '%' || #{p_comment} || '%'

SELECT PCODE, PNAME, PCOUNT, COST_PRICE, SELLING_PRICE, P_COMMENT FROM PRODUCTLIST

	AND PCODE LIKE '%' || #{pcode} || '%'
	AND PNAME LIKE '%' || #{pname} || '%'
	 PCOUNT >= #{pcount} 
	
		AND COST_PRICE BETWEEN #{price} AND #{price2}
	
	
		 AND COST_PRICE >= #{price} 
	
	
		 AND COST_PRICE <= #{price2} 
	
	AND P_COMMENT LIKE '%' || #{p_comment} || '%'

SELECT COUNT(*) FROM SUPPLY_APPLICATION_VIEW

	AND STATUS = 'N'
	AND STATUS = 'Y'
	AND STATUS = 'F'
	AND ENAME = #{ename}
	AND SNAME = #{sname}
	
		AND APPLICATE_DATE BETWEEN #{date} AND #{date2}
		 AND APPLICATE_DATE >= #{date} 
		 AND APPLICATE_DATE <= #{date2}

SELECT * FROM SUPPLY_APPLICATION_VIEW

	AND STATUS = 'N'
	AND STATUS = 'Y'
	AND STATUS = 'F'
	AND ENAME = #{ename}
	AND SNAME = #{sname}
	
		AND APPLICATE_DATE BETWEEN #{date} AND #{date2}
		 AND APPLICATE_DATE >= #{date} 
		 AND APPLICATE_DATE <= #{date2}

SELECT COUNT(*) FROM PRODUCT_INOUT_VIEW

	AND INOUT_STATE = #{selectVal}
	AND CCODE = #{selectClient}
	AND PCODE = #{selectProduct}
	
		AND INOUT_DATE BETWEEN #{date} AND #{date2}
		 AND INOUT_DATE >= #{date} 
		 AND INOUT_DATE <= #{date2}

SELECT * FROM PRODUCT_INOUT_VIEW

	AND INOUT_STATE = #{selectVal}
	AND CCODE = #{selectClient}
	AND PCODE = #{selectProduct}
	
		AND INOUT_DATE BETWEEN #{date} AND #{date2}
		 AND INOUT_DATE >= #{date} 
		 AND INOUT_DATE <= #{date2} 
	

ORDER BY INOUT_DATE DESC
